import { useRef, useState, useEffect } from "react";
import {Navigate} from 'react-router-dom';
import {
  faCheck,
  faTimes,
  faInfoCircle,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";
import { registerShop } from "../utils/api";

const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
const SHOP_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;
const POSTCODE_REGEX =
  /([Gg][Ii][Rr] 0[Aa]{2})|((([A-Za-z][0-9]{1,2})|(([A-Za-z][A-Ha-hJ-Yj-y][0-9]{1,2})|(([A-Za-z][0-9][A-Za-z])|([A-Za-z][A-Ha-hJ-Yj-y][0-9][A-Za-z]?))))\s?[0-9][A-Za-z]{2})/;
const LATITUDE_REGEX = /^-?([0-8]?[0-9]|90)(\.[0-9]{1,10})$/;
const LONGITUDE_REGEX = /^-?([0-9]{1,2}|1[0-7][0-9]|180)(\.[0-9]{1,10})$/;

const Register = () => {
  const userRef = useRef();
  const shopNameRef = useRef();
  const errRef = useRef();

  const [user, setUser] = useState("");
  const [validName, setValidName] = useState(false);
  const [userFocus, setUserFocus] = useState(false);

  const [shopType, setShopType] = useState();
  const [shopTypeFocus, setShopTypeFocus] = useState(false);

  const [crowdFlag, setCrowdFlag] = useState(0);
  const [crowdFlagFocus, setCrowdFlagFocus] = useState(false);

  const [shopName, setShopName] = useState("");
  const [validShopName, setValidShopName] = useState(false);
  const [shopNameFocus, setShopNameFocus] = useState(false);

  const [postcode, setPostcode] = useState("");
  const [validPostcode, setValidPostcode] = useState(false);
  const [postcodeFocus, setPostcodeFocus] = useState(false);

  const [latitude, setLatitude] = useState("");
  const [validLatitude, setValidLatitude] = useState(false);
  const [latitudeFocus, setLatitudeFocus] = useState(false);

  const [longitude, setLongitude] = useState("");
  const [validLongitude, setValidLongitude] = useState(false);
  const [longitudeFocus, setLongitudeFocus] = useState(false);

  const [pwd, setPwd] = useState("");
  const [validPwd, setValidPwd] = useState(false);
  const [pwdFocus, setPwdFocus] = useState(false);

  const [matchPwd, setMatchPwd] = useState("");
  const [validMatch, setValidMatch] = useState(false);
  const [matchFocus, setMatchFocus] = useState(false);

  const [errMsg, setErrMsg] = useState("");
  const [success, setSuccess] = useState(false);

//


useEffect(() => {
  userRef.current.focus();
}, []);

useEffect(() => {
  const result = USER_REGEX.test(user);
  setValidName(result);
}, [user]);

useEffect(() => {
  const result = SHOP_REGEX.test(shopName);
  setValidShopName(result);
}, [shopName]);

useEffect(() => {
  const result = POSTCODE_REGEX.test(postcode);
  setValidPostcode(result);
}, [postcode]);

useEffect(() => {
  const result = LATITUDE_REGEX.test(latitude);
  setValidLatitude(result);
}, [latitude]);

useEffect(() => {
  const result = LONGITUDE_REGEX.test(longitude);
  setValidLongitude(result);
}, [longitude]);

useEffect(() => {
  const result = PWD_REGEX.test(pwd);
  setValidPwd(result);
  setValidMatch(pwd === matchPwd);
}, [pwd, matchPwd]);

useEffect(() => {
  setErrMsg("");
}, [user, pwd, matchPwd]);



//
  useEffect(() => {
    userRef.current.focus();
  }, []);

  useEffect(() => {
    const result = USER_REGEX.test(user);
    console.log(result, "<<<user regex");
    console.log(user, "<<user");
    setValidName(result);
  }, [user]);

  useEffect(() => {
    const result = PWD_REGEX.test(pwd);
    console.log(result, "<<<pwd regex");
    console.log(pwd, "password");
    setValidPwd(result);
    setValidMatch(pwd === matchPwd);
  }, [pwd, matchPwd]);

  useEffect(() => {
    setErrMsg("");
  }, [user, pwd, matchPwd]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const v1 = USER_REGEX.test(user);
    const v2 = PWD_REGEX.test(pwd);
    if (!v1 || !v2) {
      setErrMsg("Invalid Entry");
      return;
    }

    registerShop(user, pwd, shopType)
      .then((res) => {
        setSuccess(true);
      })
      .catch((err) => {
        if (!err.response) {
          setErrMsg("No Server Response");
        } else if (err.response.status === 409) {
          setErrMsg("Username Taken");
        } else {
          setErrMsg("Registration Failed");
        }
        errRef.current.focus();
      });
    setUser("");
    setPwd("");
    setMatchPwd("");
    setShopName("");
    setShopType("");
    setPostcode("");
    setLongitude("");
    setLatitude("");
    setCrowdFlag("");
  };
  return (
    <>
      {success ? (
        <section>
          <h1>Success!</h1>
          <p>
            <a href="#">Sign In</a>
          </p>
          <Navigate to='/login'  />//Navigate
        </section>
      ) : (
        <section>
          <p
            ref={errRef}
            className={errMsg ? "errmsg" : "offscreen"}
            aria-live="assertive"
          >
            {errMsg}
          </p>
          <h1> Registration Form:</h1>
          <form onSubmit={handleSubmit}>
            <label htmlFor="username">
              Username:
              <FontAwesomeIcon
                icon={faCheck}
                className={validName ? "valid" : "hide"}
              />
              <FontAwesomeIcon
                icon={faTimes}
                className={validName || !user ? "hide" : "invalid"}
              />
            </label>
            <input
              type="text"
              id="username"
              ref={userRef}
              autoComplete="off"
              onChange={(e) => setUser(e.target.value)}
              value={user}
              required
              aria-invalid={validName ? "false" : "true"}
              aria-describedby="uidnote"
              onFocus={() => setUserFocus(true)}
              onBlur={() => setUserFocus(false)}
            />
            <p
              id="uidnote"
              className={
                userFocus && user && !validName ? "instructions" : "offscreen"
              }
            >
              <FontAwesomeIcon icon={faInfoCircle} />
              4 to 24 characters.
              <br />
              Must begin with a letter.
              <br />
              Letters, numbers, underscores, hyphens allowed.
            </p>

            <label htmlFor="shopType">Shop Type:</label>
            <div className="radio-toolbar">
              <input
                type="radio"
                name="shopType"
                value="Gas Station"
                onChange={(e) => setShopType(e.target.value)}
                required
                onFocus={() => setShopTypeFocus(true)}
                onBlur={() => setShopTypeFocus(false)}
              />
              <label htmlFor="shopType"> Gas Station</label>
              <br></br>
              <input
                type="radio"
                name="shopType"
                value="Supermarket"
                onChange={(e) => setShopType(e.target.value)}
                required
                onFocus={() => setShopTypeFocus(true)}
                onBlur={() => setShopTypeFocus(false)}
              />
              <label htmlFor="shopType">Supermarket</label>
            </div>
            <p
              className={
                shopTypeFocus && !shopType ? "instructions" : "offscreen"
              }
            >
              Select type of shop
            </p>

            <label htmlFor="shopname">
              Shop Name:
              <FontAwesomeIcon
                icon={faCheck}
                className={validShopName ? "valid" : "hide"}
              />
              <FontAwesomeIcon
                icon={faTimes}
                className={validShopName || !shopName ? "hide" : "invalid"}
              />
            </label>
            <input
              type="text"
              id="shopname"
              ref={shopNameRef}
              autoComplete="off"
              onChange={(e) => setShopName(e.target.value)}
              value={shopName}
              required
              aria-invalid={validShopName ? "false" : "true"}
              aria-describedby="uidnote"
              onFocus={() => setShopNameFocus(true)}
              onBlur={() => setShopNameFocus(false)}
            />
            <p
              id="uidnote"
              className={
                shopNameFocus && shopName && !validShopName
                  ? "instructions"
                  : "offscreen"
              }
            >
              <FontAwesomeIcon icon={faInfoCircle} />
              4 to 24 characters.
              <br />
              Must begin with a letter.
              <br />
              Letters, numbers, underscores, hyphens allowed.
            </p>

            <label htmlFor="postcode">
              Postcode:
              <FontAwesomeIcon
                icon={faCheck}
                className={validPostcode ? "valid" : "hide"}
              />
              <FontAwesomeIcon
                icon={faTimes}
                className={validPostcode || !postcode ? "hide" : "invalid"}
              />
            </label>
            <input
              type="text"
              id="postcode"
              autoComplete="off"
              onChange={(e) => setPostcode(e.target.value)}
              value={postcode}
              required
              aria-invalid={validPostcode ? "false" : "true"}
              aria-describedby="uidnote"
              onFocus={() => setPostcodeFocus(true)}
              onBlur={() => setPostcodeFocus(false)}
            />
            <p
              id="uidnote"
              className={
                postcodeFocus && postcode && !validPostcode
                  ? "instructions"
                  : "offscreen"
              }
            >
              <FontAwesomeIcon icon={faInfoCircle} />
              Provide your postcode
            </p>

            <label htmlFor="latitude">
              Latitude:
              <FontAwesomeIcon
                icon={faCheck}
                className={validLatitude ? "valid" : "hide"}
              />
              <FontAwesomeIcon
                icon={faTimes}
                className={validLatitude || !latitude ? "hide" : "invalid"}
              />
            </label>
            <input
              type="text"
              id="latitude"
              autoComplete="off"
              onChange={(e) => setLatitude(e.target.value)}
              value={latitude}
              required
              aria-invalid={validLatitude ? "false" : "true"}
              aria-describedby="uidnote"
              onFocus={() => setLatitudeFocus(true)}
              onBlur={() => setLatitudeFocus(false)}
            />
            <p
              id="uidnote"
              className={
                latitudeFocus && latitude && !validLatitude
                  ? "instructions"
                  : "offscreen"
              }
            >
              <FontAwesomeIcon icon={faInfoCircle} />
              Provide your latitude in the form of decimal
            </p>

            <label htmlFor="longitude">
              Longitude:
              <FontAwesomeIcon
                icon={faCheck}
                className={validLongitude ? "valid" : "hide"}
              />
              <FontAwesomeIcon
                icon={faTimes}
                className={validLongitude || !longitude ? "hide" : "invalid"}
              />
            </label>
            <input
              type="text"
              id="longitude"
              autoComplete="off"
              onChange={(e) => setLongitude(e.target.value)}
              value={longitude}
              required
              aria-invalid={validLongitude ? "false" : "true"}
              aria-describedby="uidnote"
              onFocus={() => setLongitudeFocus(true)}
              onBlur={() => setLongitudeFocus(false)}
            />
            <p
              id="uidnote"
              className={
                longitudeFocus && longitude && !validLongitude
                  ? "instructions"
                  : "offscreen"
              }
            >
              <FontAwesomeIcon icon={faInfoCircle} />
              Provide your longitude in the form of decimal
            </p>

            <label htmlFor="password">
              Password:
              <FontAwesomeIcon
                icon={faCheck}
                className={validPwd ? "valid" : "hide"}
              />
              <FontAwesomeIcon
                icon={faTimes}
                className={validPwd || !pwd ? "hide" : "invalid"}
              />
            </label>
            <input
              type="password"
              id="password"
              onChange={(e) => setPwd(e.target.value)}
              value={pwd}
              required
              aria-invalid={validPwd ? "false" : "true"}
              aria-describedby="pwdnote"
              onFocus={() => setPwdFocus(true)}
              onBlur={() => setPwdFocus(false)}
            />
            <p
              id="pwdnote"
              className={pwdFocus && !validPwd ? "instructions" : "offscreen"}
            >
              <FontAwesomeIcon icon={faInfoCircle} />
              8 to 24 characters.
              <br />
              Must include uppercase and lowercase letters, a number and a
              special character.
              <br />
              Allowed special characters:{" "}
              <span aria-label="exclamation mark">!</span>{" "}
              <span aria-label="at symbol">@</span>{" "}
              <span aria-label="hashtag">#</span>{" "}
              <span aria-label="dollar sign">$</span>{" "}
              <span aria-label="percent">%</span>
            </p>
            <label htmlFor="confirm_pwd">
              Confirm Password:
              <FontAwesomeIcon
                icon={faCheck}
                className={validMatch && matchPwd ? "valid" : "hide"}
              />
              <FontAwesomeIcon
                icon={faTimes}
                className={validMatch || !matchPwd ? "hide" : "invalid"}
              />
            </label>
            <input
              type="password"
              id="confirm_pwd"
              onChange={(e) => setMatchPwd(e.target.value)}
              value={matchPwd}
              required
              aria-invalid={validMatch ? "false" : "true"}
              aria-describedby="confirmnote"
              onFocus={() => setMatchFocus(true)}
              onBlur={() => setMatchFocus(false)}
            />
            <p
              id="confirmnote"
              className={
                matchFocus && !validMatch ? "instructions" : "offscreen"
              }
            >
              <FontAwesomeIcon icon={faInfoCircle} />
              Must match the first password input field.
            </p>

            <label htmlFor="crowdFlag">Crowd Flag:</label>
            <div className="radio-toolbar">
              <input
                type="radio"
                name="crowdFlag"
                value="0"
                onChange={(e) => setCrowdFlag(e.target.value)}
                required
                onFocus={() => setCrowdFlagFocus(true)}
                onBlur={() => setCrowdFlagFocus(false)}
              />
              <label htmlFor="shopType"> 0 </label>
              <br></br>
              <input
                type="radio"
                name="crowdFlag"
                value="1"
                onChange={(e) => setCrowdFlag(e.target.value)}
                required
                onFocus={() => setCrowdFlagFocus(true)}
                onBlur={() => setCrowdFlagFocus(false)}
              />
              <label htmlFor="crowdFlag">1</label>
            </div>
            <p
              className={
                crowdFlagFocus && !crowdFlag ? "instructions" : "offscreen"
              }
            >
              Select 0 or 1
            </p>

            <button
              disabled={!validName || !validPwd || !validMatch ? true : false}
            >
              Sign Up
            </button>
          </form>
          <p>
            Already registered?
            <br />
            <span className="line">
              <Link to="/login">Sign-In</Link>
            </span>
          </p>
        </section>
      )}
    </>
  );
};

export default Register;
